import './list.scss';
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { auth, db } from '../utils/firebase.js';
import { signOut } from 'firebase/auth';
import { uid } from 'uid';
import { set, ref, onValue, remove, update } from 'firebase/database';

import AddIcon from '../assect/add.svg';
import DeleteIcon from '../assect/delete.svg';
import EditIcon from '../assect/edit.svg';
// import LogoutIcon from '@mui/icons-material/Logout';
// import CheckIcon from '@mui/icons-material/Check';

export default function List() {
  const [todo, setTodo] = useState('');
  const [todos, setTodos] = useState([]);
  const [isEdit, setIsEdit] = useState(false);
  const [tempUidd, setTempUidd] = useState('');
  const navigate = useNavigate();
  useEffect(() => {
    auth.onAuthStateChanged((user) => {
      if (user) {
        // read
        onValue(ref(db, `/${auth.currentUser.uid}`), (snapshot) => {
          setTodos([]);
          const data = snapshot.val();
          if (data !== null) {
            Object.values(data).map((todo) => {
              setTodos((oldArray) => [...oldArray, todo]);
            });
          }
        });
      } else if (!user) {
        navigate('/');
      }
    });
  }, []);
  const handleSignOut = async () => {
    await signOut(auth)
      .then(() => {
        navigate('/');
      })
      .catch((err) => {
        alert(err.message);
      });
  };

  // add
  const writeToDatabase = () => {
    const uidd = uid();
    set(ref(db, `/${auth.currentUser.uid}/${uidd}`), {
      todo: todo,
      uidd: uidd,
    });
    setTodo('');
  };
  // console.log(auth.currentUser);

  // update
  const handleUpdate = (todo) => {
    setIsEdit(true);
    setTodo(todo.todo);
    setTempUidd(todo.uidd);
  };

  // edit
  const handleEditConfirm = () => {
    update(ref(db, `/${auth.currentUser.uid}/${tempUidd}`), {
      todo: todo,
      tempUidd: tempUidd,
    });

    setTodo('');
    setIsEdit(false);
  };

  // delete
  const handleDelete = (uid) => {
    remove(ref(db, `/${auth.currentUser.uid}/${uid}`));
  };
  return (
    <div className='list'>
      <h1>My TOdo List</h1>
      <input
        type='text'
        placeholder='add todo...'
        value={todo}
        onChange={(e) => setTodo(e.target.value)}
      />
      {todos.map((todo, index) => (
        <div className='todo' key={todo.index}>
          <h2>{todo.todo}</h2>
          <button onClick={handleUpdate}>update</button>
          <button onClick={() => handleDelete(todo.uidd)}>delete</button>
        </div>
      ))}
      <button onClick={writeToDatabase}>
        add <img src={AddIcon} />
      </button>
      <button onClick={handleSignOut}>登出</button>
    </div>
  );
}
