import './welcome.scss';
import React, { useState, useEffect } from 'react';
import {
  signInWithEmailAndPassword,
  onAuthStateChanged,
  createUserWithEmailAndPassword,
} from 'firebase/auth';
import { auth } from '../firebase.js';
import { useNavigate } from 'react-router-dom';

export default function Welcome() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isRegistering, setisRegistering] = useState(false);
  const [registerInformation, setRegisterInformation] = useState({
    email: '',
    confirmEmail: '',
    password: '',
    confirmPassword: '',
  });
  const navigate = useNavigate();

  useEffect(() => {
    auth.onAuthStateChanged((user) => {
      if (user) {
        navigate('/list');
      }
    });
  }, []);
  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };
  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };
  const handleSignIn = (e) => {
    signInWithEmailAndPassword(auth, email, password)
      .then(() => {
        navigate('/list');
      })
      .catch((err) => {
        alert(err.message);
      });
  };
  const handleRegister = (e) => {
    if (registerInformation.email !== registerInformation.confirmEmail) {
      alert('請輸入相同的Email');
      return;
    } else if (registerInformation.password !== registerInformation.confirmPassword) {
      alert('確認密碼輸入錯誤');
      return;
    }
    createUserWithEmailAndPassword(auth, email, password)
      .then(() => {
        navigate('/list');
      })
      .catch((err) => {
        alert(err.message);
      });
  };
  return (
    <div className='welcome'>
      <h1>Welcome Todo-List </h1>
      <div className='login-register-container'>
        {isRegistering ? (
          <>
            <input
              type='email'
              placeholder='請輸入信箱'
              value={registerInformation.email}
              onChange={(e) =>
                setRegisterInformation({
                  ...registerInformation,
                  email: e.target.value,
                })
              }
            />
            <input
              type='email'
              placeholder='再次確認信箱'
              onChange={handleEmailChange}
              value={registerInformation.confirmEmail}
            />
            <input
              type='password'
              placeholder='請輸入密碼'
              onChange={handlePasswordChange}
              value={registerInformation.password}
            />
            <input
              type='password'
              placeholder='再次確認密碼'
              onChange={handlePasswordChange}
              value={registerInformation.confirmPassword}
            />
            <button onClick={handleRegister}>註冊</button>
            <button onClick={() => setisRegistering(false)}>回上一頁</button>
          </>
        ) : (
          <>
            <input
              type='email'
              placeholder='請輸入信箱'
              onChange={handleEmailChange}
              value={email}
            />
            <input
              type='password'
              placeholder='請輸入密碼'
              onChange={handlePasswordChange}
              value={password}
            />
            <button onClick={handleSignIn}>登入</button>
            <button onClick={() => setisRegistering(true)}>註冊新帳戶</button>
          </>
        )}
      </div>
    </div>
  );
}
