import './list.scss';
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { auth, db } from '../utils/firebase.js';
import { signOut } from 'firebase/auth';
import { uid } from 'uid';
import { set, ref } from 'firebase/database';

import AddIcon from '../assect/add.svg';
import DeleteIcon from '../assect/delete.svg';
import EditIcon from '@mui/icons-material/Delete';
// import LogoutIcon from '@mui/icons-material/Logout';
// import CheckIcon from '@mui/icons-material/Check';

export default function List() {
  const [todo, setTodo] = useState('');
  const [todos, setTodos] = useState([]);
  const navigate = useNavigate();
  useEffect(() => {
    auth.onAuthStateChanged((user) => {
      if (user) {
        onValue(ref(db, `/${auth.currentUser.uid}/${uidd}`), (snapshot) => {
          setTodos();
          const data = snapshot.val();
          if (data !== null) {
            Object.values(data).map((todo) => {
              setTodo((oldArray) => [...oldArray, todo]);
            });
          }
        });
      } else if (!user) {
        navigate('/');
      }
    });
  }, []);
  const handleSignOut = async () => {
    await signOut(auth)
      .then(() => {
        navigate('/');
      })
      .catch((err) => {
        alert(err.message);
      });
  };

  // read
  // add
  const writeToDatabase = async () => {
    const uidd = uid();
    set(ref(db, `/${auth.currentUser.uid}/${uidd}`), {
      todo: todo,
      uidd: uidd,
    });
    setTodo('');
  };
  console.log(auth.currentUser);
  // update
  // delete

  return (
    <div className='list'>
      <h1>List</h1>
      <input
        type='text'
        placeholder='add todo...'
        value={todo}
        onChange={(e) => setTodo(e.target.value)}
      />
      {todos.map((todo) => (
        <div className='todo'>
          <h2>{todo.todo}</h2>
          <EditIcon fontSize='large' onClick={() => handleUpdate(todo)} className='edit-button' />
          <DeleteIcon
            fontSize='large'
            onClick={() => handleDelete(todo.uidd)}
            className='delete-button'
          />
        </div>
      ))}
      <button onClick={writeToDatabase}>add</button>
      <button onClick={handleSignOut}>登出</button>
    </div>
  );
}
